import React from 'react'
import { Link } from 'react-router-dom'

import './Feed.css'

const Feed = ({articles}) => {
    return (
        <div className='article'>
            {articles.map((articles, index) => {
                return (<div className='article__preview' key={index}>
                    <div className='article__meta'>
                        <Link to={`/profiles/${articles.author.username}`} className='article__link'>
                            <div className='article__avatar avatar'>
                                <img 
                                    src={articles.author.image} 
                                    alt="author avatar"
                                    className='avatar__img'
                                />
                            </div>
                        </Link>
                        <div className="article__info">
                            <Link 
                                to={`/profiles/${articles.author.username}`}
                                className="article__link"
                            >
                                {articles.author.username}
                            </Link>
                            <span className="article__data">{articles.createdAt}</span>
                        </div>
                    </div>
                    <Link to={`/articles/${articles.slug}`} className="article__link">
                        <h1>{articles.title}</h1>
                        <p>{articles.description}</p>
                        <span>Read more...</span>
                        <ul className='article__tag-list'>
                            {articles.tagList.map((tag, index) => {
                                return <li key={tag} className="article__tag-item">{tag}</li>
                            })}
                        </ul>
                    </Link>
                </div>)
            })}
        </div>
    )
}

export default Feed