import React, {useContext, Fragment} from 'react'

import { NavLink, Link } from 'react-router-dom'

import {CurrentUserContext} from 'contexts/currentUser';
import './TopBar.css';

const TopBar = () => {
    const [currentUserState] = useContext(CurrentUserContext)
    console.log(currentUserState)
    return (
        <nav className="nav">
            <div className="container">
                <Link className='nav__logo' to='/'>Medium</Link>
                <ul className="nav__list list">
                    <li className="list__item">
                        <NavLink to='/' exact>Home</NavLink>
                    </li>

                    {currentUserState.isLoginedIn === false && (
                        <Fragment>
                            <li className="list__item">
                                <NavLink to='/login'>Sign in</NavLink>
                            </li>
                            <li className="list__item">
                                <NavLink to='/register'>Sign up</NavLink>
                            </li>
                        </Fragment>
                    )}

                    {currentUserState.isLoginedIn && (
                        <Fragment>
                            <li className="list__item">
                                <NavLink to='/articles/new'>
                                    <i className='lise__icon'></i>
                                    &nbsp; New post
                                </NavLink>
                            </li>
                            <li className="list__item">
                                <NavLink to={`/profiles/${currentUserState.currentUser.username}`}>
                                    <img 
                                        className="list__avatar avatar" 
                                        src={currentUserState.currentUser.image} 
                                        alt='user avatar'
                                    />
                                    &nbsp; {currentUserState.currentUser.username}
                                </NavLink>
                            </li>
                        </Fragment>
                    )}
                </ul>
            </div>
        </nav>
    )
}

export default TopBar;