import React, {useContext, Fragment} from 'react'

import { NavLink, Link } from 'react-router-dom'

import {CurrentUserContext} from 'contexts/currentUser';
import './TopBar.css';
import writeIcon from "./icons/border_color-24px.svg";

const TopBar = () => {
    const [currentUserState] = useContext(CurrentUserContext)
    console.log(currentUserState)
    return (
        <nav className="nav">
            <div className="container">
                <Link className='nav__logo' to='/'>Medium</Link>
                <ul className="nav__list list">
                    <li className="list__item item">
                        <NavLink to='/' className='item__link' exact>Home</NavLink>
                    </li>

                    {currentUserState.isLoginedIn === false && (
                        <Fragment>
                            <li className="list__item item">
                                <NavLink to='/login' className='item__link'>Sign in</NavLink>
                            </li>
                            <li className="list__item item">
                                <NavLink to='/register' className='item__link'>Sign up</NavLink>
                            </li>
                        </Fragment>
                    )}

                    {currentUserState.isLoginedIn && (
                        <Fragment>
                            <li className="list__item item">
                                <NavLink to='/articles/new' className='item__link'>
                                    <ion-icon name="create-outline"></ion-icon>
                                    New post
                                </NavLink>
                            </li>
                            <li className="list__item item">
                                <NavLink 
                                    to={`/profiles/${currentUserState.currentUser.username}`} 
                                    className="item__link">
                                    <div className='item__avatar avatar'>
                                         <img 
                                            className="avatar__img" 
                                            src={currentUserState.currentUser.image} 
                                            alt='user avatar'
                                        />
                                    </div>
                                    &nbsp; {currentUserState.currentUser.username}
                                </NavLink>
                            </li>
                        </Fragment>
                    )}
                </ul>
            </div>
        </nav>
    )
}

export default TopBar;