import React, {useEffect} from 'react'

import useFetch from 'hooks/useFetch'
import { Link } from 'react-router-dom'

import Loading from 'components/Loading/Loading';
import ErrorMessage from 'components/ErrorMessage/ErrorMessage';
import TagList from 'components/TagList/TagList';

const Articles = (props) => {
    const slug = props.match.params.slug
    const apiUrl = `/articles/${slug}`
    console.log(apiUrl)
    const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)
    useEffect(() => {
        doFetch()
        
    }, [doFetch])
    
    return (
        <div className='article'>
            <div className='article__bunner'>
                {isLoading && <Loading />}
                {error && <ErrorMessage />}
                {!isLoading && response && (
                    <div className='container'>
                        <h1>{response.article.title}</h1>
                        <div className='article__meta'>
                            <Link to={`/profiles/${response.article.author.username}`}>
                                <img className='avatar__img' src={response.article.author.image} alt='avatar'/>
                            </Link>
                            <span className='article__date'>{response.article.createdAt}</span>
                        </div>
                    </div>
                )}
            </div>
            <div className='container'>
                {isLoading && <Loading />}
                {error && <ErrorMessage />}
                {!isLoading && response && (
                    <div className=''>
                        <div className=''>
                            <p>{response.article.body}</p>
                        </div>
                        <TagList tags={response.article.tagList} />
                    </div>
                )}
            </div>

        </div>
    )
}

export default Articles;