import React, {Fragment, useEffect} from 'react';
import {stringify} from 'query-string';

import useFetch from 'hooks/useFetch';

import Feed from 'components/Feed/Feed';
import Pagination from 'components/Pagination/Pagination';
import {getPaginator, limit} from 'utils';
import './globalFeed.css';

const GlobalFeed = ({location, match}) => {
    const {offset, currentPage} = getPaginator(location.search)
    const stringifiedParam = stringify({
        limit,
        offset
    })
    const apiUrl = `/articles?${stringifiedParam}`
    const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)
    const url = match.url;
    useEffect(() => {
       doFetch()
    }, [doFetch, currentPage])
    return (
        <main className='main'>
            <div className='banner'>
                <div className='container--col'>
                    <h1>Medium Clone</h1>
                    <p>A place to share knowledge</p>
                </div>
            </div>
            <div className='content'>
                <section className='content__feeds feeds'>
                    {isLoading && <div>Loading...</div>}
                    {error && <div>Some error happened</div>}
                    {!isLoading && response && (
                        <Fragment>
                            <Feed articles={response.articles}></Feed>
                            <Pagination 
                                total={response.articleCount} 
                                limit={limit} 
                                url={url} 
                                currentPage={currentPage}
                            />
                        </Fragment>
                    )}
                </section>
                <aside className='content__widgets widgets'>
                    Popular tags
                </aside>
            </div>
        </main>
    );
}

export default GlobalFeed